const bcrypt = require('bcryptjs');
const User = require('../Models/UserModels');
const Book = require("../Models/BookModels");
const Blog = require("../Models/BlogModels");
const jwt = require('jsonwebtoken');
const asyncHandler = require("express-async-handler");
const { constants } = require('fs/promises');

// Register a new user
const registerUser = asyncHandler (async (req, res) => {
    try {
        const { name, email, password } = req.body;
        const userExists = await User.findOne({ email });

        if (userExists) {
            return res.status(400).json({ message: 'User already exists' });
        }

        const profilePic = ""


        const user = new User({ name, email, profilePic, password });
        await user.save();

        res.status(201).json({
            _id: user._id,
            name: user.name,
            email: user.email,
            profilePic: user.profilePic,
            token: generateToken(user._id),
        });
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
});

// Login a user
const loginUser = async (req, res) => {
    try {
        const { email, password } = req.body;
        const user = await User.findOne({ email });
        console.log(user)
        if (user && (await user.matchPassword(password))) {
            res.json({
                _id: user._id,
                name: user.name,
                email: user.email,
                token: generateToken(user._id),
            });
        } else {
            res.status(401).json({ message: 'Invalid email or password' });
        }
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
};

//get info
const getInfo = async (req, res) => {
    try {
        const userId = req.user._id;

        // Find the user and populate related documents
        const user = await User.findById(userId)
        const booksUploaded = await Book.find({ uploader: userId }).populate('tags comments likes');
        const blogsUploaded = await Blog.find({ author: userId }).populate('tags comments likes');
        const booksLiked = await Book.find({ likes: userId }).populate('tags comments likes');
        const blogsLiked = await Blog.find({ likes: userId }).populate('tags comments likes');

        if (user) {
            res.json({
                user,
                booksUploaded,
                blogsUploaded,
                booksLiked,
                blogsLiked,
            });
        } else {
            res.status(404).json({ message: 'User not found' });
        }
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
};

// Generate JWT token
const generateToken = (id) => {
    return jwt.sign({ id }, process.env.JWT_SECRET, {
        expiresIn: '30d',
    });
};

const toggleRole = async (req, res) => {
    try {
        const userId = req.user._id;

        // Find the user by ID
        const user = await User.findById(userId);

        if (user) {
            // Toggle the role
            const newRole = user.role === 'user' ? 'author' : 'user';

            // Update the user's role
            user.role = newRole;
            await user.save();

            res.status(200).json(user.role);
        } else {
            res.status(404).json({ message: 'User not found' });
        }
    } catch (error) {
        res.status(500).json({ error: error.message });
    }
};

module.exports = {
    registerUser,
    loginUser,
    getInfo,
    toggleRole,
};
